import React, { useState, useEffect } from "react"
import "./TweetList.css"
import { connect } from "react-redux"
import moment from "moment"

function TweetList(props) {
    const [tweetList, setTweetList] = useState([])
    useEffect(() => {
        const onLoadTweetList = () => {
            setTweetList(props.tweet_payload)
        }
        onLoadTweetList()
    }, [props.tweet_payload])

    return (
        <div className="container">
            <ul>
                {tweetList.map((tweet, index) => (
                    <div className="tweet-list-container">
                        <li key={index}>
                            <div className="li-element">
                                <div className="username-icon">
                                    <span></span>
                                </div>

                                <div className="tweet-body">
                                    <div className="tweet-body-header">
                                        <div className="tweet-fullName">
                                            <span>{`${tweet.firstName} ${tweet.lastName}`}</span>
                                        </div>

                                        <div className="tweet-username">
                                            <span>{tweet.username}</span>
                                        </div>

                                        <div className="tweet-date-created">
                                            <span>
                                                {moment(
                                                    tweet.createdAt
                                                ).fromNow()}
                                            </span>
                                        </div>
                                    </div>
                                    <div className="tweet-body-text">
                                        <p>{tweet.tweet_body}</p>
                                    </div>
                                </div>
                            </div>
                        </li>
                    </div>
                ))}
            </ul>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        tweet_payload: state.tweets,
    }
}
export default connect(mapStateToProps)(TweetList)
