import React, { useState, useEffect } from "react"
import { connect } from "react-redux"
import TweetForm from "./TweetForm/TweetForm"
import { addTweet } from "../action/tweet"
import TweetList from "./TweetList/TweetList"
import LogoutBtn from "./LogoutBtn/LogoutBtn"
import { getCurrentUser } from "../api/user"

function Tweet(props) {
    const [userData, setUserData] = useState([])
    // props.dispatch(
    //     addTweet({
    //         name: "Test",
    //         comments: 12,
    //         retweets: 23,
    //         likses: 23,
    //     })
    // )
    useEffect(() => {
        try {
            getCurrentUser(props.currentUserID).then((res) =>
                setUserData(res.user_data)
            )
        } catch (error) {
            console.log(error.message)
        }
    }, [])
    console.log(userData)
    return (
        <div className="container">
            <LogoutBtn />
            <TweetForm
                onTweetPost={(tweetPost) => {
                    props.dispatch(addTweet(tweetPost))
                }}
            />

            {props.tweet_payload.length > 0 ? <TweetList /> : <div></div>}
        </div>
    )
}
const mapStateToProps = (state) => {
    return {
        tweet_payload: state.tweets,
        currentUserID: state.user,
    }
}
export default connect(mapStateToProps)(Tweet)
