const tweetReducerDefaultState = []

export default (state = tweetReducerDefaultState, action) => {
    switch (action.type) {
        case "ADD_TWEET":
            return [...state, action.tweet_payload]
        case "FETCH_TASKS":
            return [...action.task_payload]
        case "REMOVE_TASK":
            return state.filter(({ id }) => id !== action.id)

        case "EDIT_TASK":
            return state.map((task_payload) => {
                if (task_payload.id === action.id) {
                    return {
                        ...task_payload,
                        ...action.updates,
                    }
                } else {
                    return task_payload
                }
            })
        default:
            return state
    }
}
