const tweetReducerDefaultState = []

export default (state = tweetReducerDefaultState, action) => {
    switch (action.type) {
        case "ADD_TWEET":
            return [...state, action.tweet_payload]
        case "ALL_TWEET":
            return [...action.tweets_payload]
        case "REMOVE_TASK":
            return state.filter(({ id }) => id !== action.id)

        case "LIKE_TWEET":
            return [...action.tweets_payload]
        default:
            return state
    }
}
